import axios from 'axios'

import {
  getPosts,
  createPost,
  updatePost,
  deletePost
} from './actions'

const baseUrl = 'http://localhost:8000/api'

export const fetchPosts = () => dispatch => {
  const endPoint = `${baseUrl}/posts`
  axios.get(endPoint)
      .then(res => {
        console.log(res)
      })
}
